import React, {useState, useEffect} from 'react'
import {allRoutines} from '../api'

const Routines = () => {

    const [gotRoutines, setGotRoutines] = useState([]);
    // console.log('--qar', gotRoutines)



    const getAllRoutines = async () => {
        try{

            const routines = await allRoutines();
            setGotRoutines(routines);


        }catch (error){
            console.error(error)
        }
    }

    useEffect( getAllRoutines, [])

    return(
        <>
        <div className = 'routines'>
        <h1> -----Routines----</h1>

        {gotRoutines.map(routine => {
            return(
                <div className = 'routinecell' key = {routine.id}>
                    <h3> Routine: {routine.name} </h3>
                    <h3> Goal: {routine.goal} </h3>
                    <h3>Creator: {routine.creatorName} </h3>
                    {/* <br/>  */}
                    {/* remove breake line later */}

                    {routine.activities[0] ? 

                    routine.activities.map((anActivity, index) => {

                        return(
                            <h5 key = {index}> 
                                <ul>
                                    <li>Activity: {anActivity.name}</li>
                                    <li>Description: {anActivity.description}</li>
                                    <li>Duration: {anActivity.duration}</li>
                                    <li>Count: {anActivity.count}</li>
                                </ul>

                            </h5>
                        )
                    }) : <p>No activities found in this routine</p>


                    }

                    <br/>

                </div>
            )
        })};

        </div>
        </>
    )
}

export default Routines
